where.if:
(
( USER_ID = #{sw_entry} OR MNGR_ID = #{sw_entry} )
${sc} = #{sw_entry}
( USER_ID = #{sw_entry} OR MNGR_ID = #{sw_entry} )
) OR (
${sc} LIKE CONCAT('%', #{sw}, '%')
( BBS_SJ LIKE CONCAT('%', #{sw}, '%') OR BBS_CN LIKE CONCAT('%', #{sw}, '%') OR REGISTER_NM LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC1 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC2 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC3 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC4 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC5 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC6 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC7 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC8 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC9 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC10 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC11 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC12 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC13 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC14 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC15 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC16 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC17 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC18 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC19 LIKE CONCAT('%', #{sw}, '%') OR BBS_ETC20 LIKE CONCAT('%', #{sw}, '%') )
)

mapper.select:
SELECT , (SELECT CTGRY_NM FROM FS_BBS_CTGRY WHERE BBS_CTGRY_SN = FS_BBS.BBS_CTGRY_SN) AS ctgryNm , (SELECT CTGRY_SUB_NM FROM FS_BBS_CTGRY_SUB WHERE BBS_CTGRY_SUB_SN = FS_BBS.BBS_CTGRY_SUB_SN) AS ctgrySubNm , (SELECT DELETE_AT FROM FS_BBS B WHERE B.BBS_SN = FS_BBS.BBS_BEST_SN) AS upperDeleteAt FROM FS_BBS
ORDER BY ${orderBy}
ORDER BY BBS_BEST_SN DESC, BBS_ORDR ASC

SELECT , (SELECT CTGRY_NM FROM FS_BBS_CTGRY WHERE BBS_CTGRY_SN = FS_BBS.BBS_CTGRY_SN) AS ctgryNm , (SELECT CTGRY_SUB_NM FROM FS_BBS_CTGRY_SUB WHERE BBS_CTGRY_SUB_SN = FS_BBS.BBS_CTGRY_SUB_SN) AS ctgrySubNm , (SELECT DELETE_AT FROM FS_BBS B WHERE B.BBS_SN = FS_BBS.BBS_BEST_SN) AS upperDeleteAt , (SELECT ATCHMNFL_EXTSN FROM FS_ATCHMNFL WHERE ACC_TY = 'bbs' AND ACC_SN = FS_BBS.BBS_SN ORDER BY REPRSNT_THUMB_AT DESC LIMIT 1) AS fileExt FROM FS_BBS
ORDER BY ${orderBy}
ORDER BY BBS_BEST_SN DESC, BBS_ORDR ASC
LIMIT #{firstIndex}, #{recordCountPerPage}

mapper.update:
UPDATE	FS_BBS
SET		BBS_ORDR = BBS_ORDR + 1
WHERE	BBS_BEST_SN = #{bbsBestSn}
		AND BBS_ORDR >= #{bbsOrdr}
